const nameInput=document.querySelector(".username"),validateName=t=>{console.log(t.target.validity.patternMismatch);/[^A-Za-zА-Яа-я]/i.test(t.target.value)?nameInput.setCustomValidity("Поле может содержать только буквы латинского и русского алфавита"):nameInput.setCustomValidity("")};nameInput.addEventListener("input",validateName);