const nameInput=document.querySelector(".username"),telInput=document.querySelector(".tel"),validateName=e=>{/[^A-Za-zА-Яа-я]/i.test(e.target.value)?nameInput.setCustomValidity("Поле может содержать только буквы латинского и русского алфавита"):nameInput.setCustomValidity("")};nameInput.addEventListener("input",validateName);const validNumberPhone=(e,t)=>{if(null===t)return e;let a="7"+e.replace(/[^\d]/g,"").slice(1,11),n="";for(let e=0;e<a.length;e+=1)switch(e){case 0:n+="+"+a[e];break;case 1:n+="("+a[e];break;case 2:case 5:case 6:case 8:case 10:n+=a[e];break;case 3:n+=a[e]+")";break;case 4:case 7:case 9:n+="-"+a[e]}return n},validateTel=e=>{const t=validNumberPhone(e.target.value,e.data);e.target.value=t};telInput.addEventListener("input",validateTel);