const nameInput=document.querySelector(".username"),validateName=t=>{/[^A-Za-zА-Яа-я]/i.test(t.target.value)?nameInput.setCustomValidity("Поле может содержать только буквы латинского и русского алфавита без пробелов"):nameInput.setCustomValidity("")};nameInput.addEventListener("input",validateName);const dateInput=document.querySelector(".date"),validateDate=t=>{const e=new Date(t.target.value);(new Date).getTime()>=e.getTime()?dateInput.setCustomValidity("Дата не может быть меньше текущей"):dateInput.setCustomValidity("")};dateInput.addEventListener("input",validateDate);