let filters=document.querySelectorAll(".sort-item"),portfolioItems=document.querySelectorAll(".project-item");console.log(portfolioItems);for(let t of filters)t.onclick=function(){for(i=0;i<filters.length;i++)filters[i].classList.remove("sort-activ");if(t.classList.add("sort-activ"),"all"==t.getAttribute("data-sort")){let t=document.querySelectorAll(".sort-hiden");for(i=0;i<t.length;i++)t[i].classList.remove("sort-hiden")}else for(i=0;i<portfolioItems.length;i++)portfolioItems[i].classList.contains(t.getAttribute("data-sort"))?portfolioItems[i].classList.remove("sort-hiden"):portfolioItems[i].classList.add("sort-hiden")};